var DashBoard;$(document).ready(function(){DashBoard=new EmbeddedDashboard();SalesXDay();Top10Sales();SalesByTag_Percent();TotalSales();Sales_ByFootTraffic();averagequantityperinv();averagesalesperinv();DashBoard.embedTo("dashboard")});function SalesXDay(){var a=new ChartComponent();a.lock();DashBoard.addComponent(a);$.get("./kpi/SalesXDay/2016-07-01/2017-01-01",function(c){var b=JSON.parse(JSON.stringify(c));a.setDimensions(3,3);a.setCaption("Sales per Day");a.setLabels(b.Date);a.addSeries("Sales","Total Sales",b.Sales);a.unlock()})}function Top10Sales(){var a=new ChartComponent();a.lock();DashBoard.addComponent(a);$.get("./kpi/Top10Sales/2016-07-01/2017-01-01",function(c){var b=JSON.parse(JSON.stringify(c));a.setDimensions(6,6);a.setCaption("Top 10 Items");a.setLabels(b.Item);a.addSeries("Sales","Total Sales",b.Sales,{numberPrefix:"$ "});a.addSeries("Costs","Total Costs",b.Costs,{numberPrefix:"$ ",seriesColor:"a4c9f3"});a.setOption("stackedTotalDisplay",true);a.unlock()})}function SalesByTag_Percent(){var a=new ChartComponent();a.lock();DashBoard.addComponent(a);$.get("./kpi/SalesByTag_Percent/2016-07-01/2017-01-01",function(c){var b=JSON.parse(JSON.stringify(c));a.setDimensions(6,6);a.setCaption("Tags as Percentage of Sales");a.setLabels(b.Tag);a.setPieValues(b.Percentage);a.unlock()})}function TotalSales(){var a=new KPIComponent();a.setDimensions(6,3);a.setCaption("Total Sales");a.lock();DashBoard.addComponent(a);$.get("./kpi/TotalSales/2016-07-01/2017-01-01",function(b){response=JSON.parse(JSON.stringify(b));a.setValue(response[0].Sales);a.unlock()})}function Sales_ByFootTraffic(){var a=new KPIComponent();a.setDimensions(3,3);a.setCaption("Sales Per Foot Traffic");a.lock();DashBoard.addComponent(a);$.get("./kpi/Sales_ByFootTraffic/2016-07-01/2017-01-01",function(b){a.setValue(b);a.unlock()})}function averagequantityperinv(){var a=new KPIComponent();a.setDimensions(3,3);a.setCaption("Average Quantity Per Invoice");a.lock();DashBoard.addComponent(a);$.get("./averagequantityperinv",function(b){response=JSON.parse(JSON.stringify(b));a.setValue(response[0].averagequantityperinv);a.unlock()})}function averagesalesperinv(){var a=new KPIComponent();a.setDimensions(3,3);a.setCaption("Average Sales Per Invoice");a.lock();DashBoard.addComponent(a);$.get("./averagesalesperinv",function(b){response=JSON.parse(JSON.stringify(b));a.setValue(response[0].averagesalesperinv);a.unlock()})};